import React from "react";
import { Link } from "react-router-dom";

const Sidebar = ({ lang = "ar", isOpen, onClose }) => {
  // ÿßŸÑÿπŸÜÿßÿµÿ± ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÖÿπ ÿØÿπŸÖ ÿßŸÑŸÑÿ∫ÿßÿ™
  const mainItems = [
    { to: "/", text: { ar: "ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©", en: "Home" }, icon: "üè†" },
    { to: "/cases", text: { ar: "ÿßŸÑŸÇÿ∂ÿßŸäÿß", en: "Cases" }, icon: "üìÇ" },
    { to: "/about", text: { ar: "ÿ≠ŸàŸÑ ÿßŸÑŸÖŸÜÿµÿ©", en: "About" }, icon: "‚ÑπÔ∏è" },
    { to: "/contact", text: { ar: "ÿßÿ™ÿµŸÑ ÿ®ŸÜÿß", en: "Contact" }, icon: "üìû" },
    {
      to: "/dashboard",
      text: { ar: "ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ", en: "Dashboard" },
      icon: "üìä",
    },
    { to: "/profile", text: { ar: "ŸÖŸÑŸÅŸä", en: "My Profile" }, icon: "üë§" },
  ];

  return (
    <>
      {/* Overlay ŸÑŸÑÿ¨ŸàÿßŸÑ */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden"
          onClick={onClose}
        ></div>
      )}

      <aside
        className={`
        fixed md:relative top-0 left-0 h-full bg-gradient-to-b from-purple-700 via-pink-600 to-red-500 animate-gradient text-white p-6 z-50
        transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full md:translate-x-0"}
        w-64 flex-shrink-0
        ${lang === "ar" ? "text-right" : "text-left"}
      `}
      >
        {/* ÿ≤ÿ± ÿßŸÑÿ•ÿ∫ŸÑÿßŸÇ ŸÑŸÑÿ¨ŸàÿßŸÑ */}
        <button
          className="md:hidden absolute top-4 left-4 text-xl text-white"
          onClick={onClose}
          aria-label={lang === "ar" ? "ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©" : "Close menu"}
        >
          ‚úï
        </button>

        <h2 className="text-2xl font-bold mb-6">
          {lang === "ar" ? "ÿßŸÑŸÇÿßÿ¶ŸÖÿ©" : "Menu"}
        </h2>

        <ul className="space-y-4">
          {mainItems.map((item, index) => (
            <li key={index}>
              <Link
                to={item.to}
                className="flex items-center gap-2 px-3 py-2 rounded hover:bg-white hover:bg-opacity-20 transition"
                onClick={() => window.innerWidth < 768 && onClose()}
              >
                <span>{item.icon}</span>
                <span>{item.text[lang === "ar" ? "ar" : "en"]}</span>
              </Link>
            </li>
          ))}
        </ul>

        {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© ŸÅŸä ÿßŸÑÿ£ÿ≥ŸÅŸÑ */}
        <div className="mt-8 pt-6 border-t border-white border-opacity-30 text-sm text-white text-opacity-70">
          <p className="mb-2">
            {lang === "ar"
              ? "ŸÖŸÜÿµÿ© ÿπÿØÿßŸÑÿ© AI ŸÑŸÑŸÖÿ≥ÿßÿπÿØÿ© ÿßŸÑŸÇÿßŸÜŸàŸÜŸäÿ©"
              : "Adalah AI Legal Assistance Platform"}
          </p>
          <p>v1.0.0</p>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
