import React from "react";
import { Link } from "react-router-dom";

const Footer = ({ lang }) => {
  return (
    <footer className="bg-gray-900 text-white py-6 mt-8">
      <div className="container mx-auto px-6 flex flex-col md:flex-row items-center justify-between">
        {/* اسم المنصة */}
        <div className="flex items-center gap-2 text-lg font-bold">
          <img src="/logo192.png" alt="عدالة AI" className="w-8 h-8" />
          <span>{lang === "ar" ? "عدالة AI" : "Justice AI"}</span>
        </div>

        {/* روابط */}
        <ul className="flex flex-wrap gap-6 mt-4 md:mt-0 text-sm">
          <li>
            <Link to="/" className="hover:underline">
              {lang === "ar" ? "الرئيسية" : "Home"}
            </Link>
          </li>
          <li>
            <Link to="/about" className="hover:underline">
              {lang === "ar" ? "من نحن" : "About"}
            </Link>
          </li>
          <li>
            <Link to="/contact" className="hover:underline">
              {lang === "ar" ? "تواصل معنا" : "Contact"}
            </Link>
          </li>
          <li>
            <Link to="/privacy" className="hover:underline">
              {lang === "ar" ? "سياسة الخصوصية" : "Privacy Policy"}
            </Link>
          </li>
        </ul>

        {/* حقوق النشر */}
        <p className="text-gray-400 text-xs mt-4 md:mt-0">
          {lang === "ar"
            ? "© 2025 عدالة AI. جميع الحقوق محفوظة."
            : "© 2025 Justice AI. All rights reserved."}
        </p>
      </div>
    </footer>
  );
};

export default Footer;
