import React from "react";
import { Link } from "react-router-dom";

const Sidebar = ({ lang = "ar", isOpen, onClose }) => {
  // العناصر الرئيسية مع دعم اللغات
  const mainItems = [
    { to: "/", text: { ar: "الصفحة الرئيسية", en: "Home" } },
    { to: "/cases", text: { ar: "القضايا", en: "Cases" } },
    { to: "/about", text: { ar: "حول المنصة", en: "About" } },
    { to: "/contact", text: { ar: "اتصل بنا", en: "Contact" } },
    { to: "/dashboard", text: { ar: "لوحة التحكم", en: "Dashboard" } },
    { to: "/profile", text: { ar: "ملفي", en: "My Profile" } },
  ];

  return (
    <>
      {/* Overlay للجوال */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden"
          onClick={onClose}
        ></div>
      )}

      <aside
        className={`
        fixed md:relative top-0 left-0 h-full bg-gray-100 p-4 md:p-6 z-50
        transform transition-transform duration-300 ease-in-out
        ${isOpen ? "translate-x-0" : "-translate-x-full md:translate-x-0"}
        w-64 flex-shrink-0
        ${lang === "ar" ? "text-right" : "text-left"}
      `}
      >
        {/* زر الإغلاق للجوال */}
        <button
          className="md:hidden absolute top-4 left-4 text-xl"
          onClick={onClose}
          aria-label={lang === "ar" ? "إغلاق القائمة" : "Close menu"}
        >
          ✕
        </button>

        <h2 className="text-xl font-bold mb-6">
          {lang === "ar" ? "القائمة" : "Menu"}
        </h2>

        <ul className="space-y-3">
          {mainItems.map((item, index) => (
            <li key={index}>
              <Link
                to={item.to}
                className="block px-3 py-2 rounded hover:bg-gray-200 transition"
                onClick={() => window.innerWidth < 768 && onClose()}
              >
                {item.text[lang === "ar" ? "ar" : "en"]}
              </Link>
            </li>
          ))}
        </ul>

        {/* معلومات إضافية في الأسفل */}
        <div className="mt-8 pt-6 border-t border-gray-300 text-sm text-gray-500">
          <p className="mb-2">
            {lang === "ar"
              ? "منصة عدالة AI للمساعدة القانونية"
              : "Adalah AI Legal Assistance Platform"}
          </p>
          <p>v1.0.0</p>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
