import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = ({ lang = "ar", isOpen, onClose, user }) => {
  const location = useLocation();
  const [activeItem, setActiveItem] = useState(location.pathname);

  // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπŸÜÿµÿ± ÿßŸÑŸÜÿ¥ÿ∑ ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÖÿ≥ÿßÿ±
  useEffect(() => {
    setActiveItem(location.pathname);
  }, [location.pathname]);

  // ÿßŸÑÿπŸÜÿßÿµÿ± ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ© ŸÖÿπ ÿØÿπŸÖ ÿßŸÑŸÑÿ∫ÿßÿ™
  const mainItems = [
    {
      to: "/",
      text: { ar: "ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©", en: "Home" },
      icon: "üè†",
      roles: ["user", "admin"],
    },
    {
      to: "/cases",
      text: { ar: "ÿßŸÑŸÇÿ∂ÿßŸäÿß", en: "Cases" },
      icon: "üìÇ",
      roles: ["user", "admin"],
    },
    {
      to: "/about",
      text: { ar: "ÿ≠ŸàŸÑ ÿßŸÑŸÖŸÜÿµÿ©", en: "About" },
      icon: "‚ÑπÔ∏è",
      roles: ["user", "admin"],
    },
    {
      to: "/contact",
      text: { ar: "ÿßÿ™ÿµŸÑ ÿ®ŸÜÿß", en: "Contact" },
      icon: "üìû",
      roles: ["user", "admin"],
    },
    {
      to: "/dashboard",
      text: { ar: "ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ", en: "Dashboard" },
      icon: "üìä",
      roles: ["user", "admin"],
    },
    {
      to: "/profile",
      text: { ar: "ŸÖŸÑŸÅŸä", en: "My Profile" },
      icon: "üë§",
      roles: ["user", "admin"],
    },
    // ÿπŸÜÿßÿµÿ± ÿßŸÑŸÖÿØŸäÿ± ŸÅŸÇÿ∑
    {
      to: "/admin",
      text: { ar: "ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿØŸäÿ±", en: "Admin Panel" },
      icon: "‚öôÔ∏è",
      roles: ["admin"],
    },
    {
      to: "/admin/users",
      text: { ar: "ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ", en: "Users Management" },
      icon: "üë•",
      roles: ["admin"],
    },
    {
      to: "/admin/cases",
      text: { ar: "ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÇÿ∂ÿßŸäÿß", en: "Cases Management" },
      icon: "üìã",
      roles: ["admin"],
    },
  ];

  // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿπŸÜÿßÿµÿ± ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿØŸàÿ± ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  const filteredItems = mainItems.filter(
    (item) => !item.roles || (user && item.roles.includes(user.role))
  );

  return (
    <>
      {/* Overlay ŸÑŸÑÿ¨ŸàÿßŸÑ */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden backdrop-blur-sm"
          onClick={onClose}
        ></div>
      )}

      <aside
        className={`
        fixed md:relative top-0 left-0 h-full bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900 text-gray-200 shadow-xl p-6 z-50
        transform transition-all duration-300 ease-in-out
        ${
          isOpen
            ? "translate-x-0 shadow-2xl"
            : "-translate-x-full md:translate-x-0"
        }
        w-64 flex-shrink-0
        ${lang === "ar" ? "text-right" : "text-left"}
        overflow-y-auto
      `}
      >
        {/* ÿ≤ÿ± ÿßŸÑÿ•ÿ∫ŸÑÿßŸÇ ŸÑŸÑÿ¨ŸàÿßŸÑ */}
        <button
          className="md:hidden absolute top-4 left-4 text-xl text-gray-400 hover:text-blue-400 transition-colors duration-200"
          onClick={onClose}
          aria-label={lang === "ar" ? "ÿ•ÿ∫ŸÑÿßŸÇ ÿßŸÑŸÇÿßÿ¶ŸÖÿ©" : "Close menu"}
        >
          ‚úï
        </button>

        {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿ≥ÿßŸäÿØÿ®ÿßÿ± */}
        <div className="mb-8 pt-2">
          <h2 className="text-xl font-bold mb-2 text-blue-400">
            {lang === "ar" ? "ÿßŸÑŸÇÿßÿ¶ŸÖÿ©" : "Menu"}
          </h2>
          {user && (
            <p className="text-sm text-gray-400">
              {lang === "ar" ? "ŸÖÿ±ÿ≠ÿ®ÿßŸã" : "Welcome"}, {user.name}
            </p>
          )}
        </div>

        {/* ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿπŸÜÿßÿµÿ± */}
        <nav>
          <ul className="space-y-2">
            {filteredItems.map((item, index) => (
              <li key={index}>
                <Link
                  to={item.to}
                  className={`flex items-center gap-3 px-3 py-3 rounded-lg transition-all duration-200 group
                    ${
                      activeItem === item.to
                        ? "bg-blue-600 text-white shadow-md"
                        : "hover:bg-gray-700 hover:text-blue-400"
                    }
                    ${lang === "ar" ? "flex-row-reverse" : ""}
                  `}
                  onClick={() => window.innerWidth < 768 && onClose()}
                >
                  <span className="text-lg">{item.icon}</span>
                  <span className="font-medium">
                    {item.text[lang === "ar" ? "ar" : "en"]}
                  </span>
                  <span
                    className={`flex-1 ${
                      lang === "ar" ? "text-left" : "text-right"
                    }`}
                  >
                    {activeItem === item.to && (
                      <span className="text-xs bg-white bg-opacity-20 px-2 py-1 rounded-full">
                        {lang === "ar" ? "ŸÜÿ¥ÿ∑" : "Active"}
                      </span>
                    )}
                  </span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ© ŸÅŸä ÿßŸÑÿ£ÿ≥ŸÅŸÑ */}
        <div className="mt-8 pt-6 border-t border-gray-700 text-xs text-gray-500">
          <p className="mb-2">
            {lang === "ar"
              ? "ŸÖŸÜÿµÿ© ÿπÿØÿßŸÑÿ© AI ŸÑŸÑŸÖÿ≥ÿßÿπÿØÿ© ÿßŸÑŸÇÿßŸÜŸàŸÜŸäÿ©"
              : "Adalah AI Legal Assistance Platform"}
          </p>
          <p>v1.0.0</p>
          <p className="mt-2">
            {lang === "ar" ? "ŸÖÿµŸÖŸÖ ÿ®ÿ™ŸÇŸÜŸäÿ© React" : "Built with React"}
          </p>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
