import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";

const Sidebar = ({ lang = "ar", isOpen, onClose, user }) => {
  const location = useLocation();
  const [activeItem, setActiveItem] = useState(location.pathname);

  // تحديث العنصر النشط عند تغيير المسار
  useEffect(() => {
    setActiveItem(location.pathname);
  }, [location.pathname]);

  // العناصر الرئيسية مع دعم اللغات
  const mainItems = [
    {
      to: "/",
      text: { ar: "الصفحة الرئيسية", en: "Home" },
      icon: "🏠",
      roles: ["user", "admin"],
    },
    {
      to: "/cases",
      text: { ar: "القضايا", en: "Cases" },
      icon: "📂",
      roles: ["user", "admin"],
    },
    {
      to: "/about",
      text: { ar: "حول المنصة", en: "About" },
      icon: "ℹ️",
      roles: ["user", "admin"],
    },
    {
      to: "/contact",
      text: { ar: "اتصل بنا", en: "Contact" },
      icon: "📞",
      roles: ["user", "admin"],
    },
    {
      to: "/dashboard",
      text: { ar: "لوحة التحكم", en: "Dashboard" },
      icon: "📊",
      roles: ["user", "admin"],
    },
    {
      to: "/profile",
      text: { ar: "ملفي", en: "My Profile" },
      icon: "👤",
      roles: ["user", "admin"],
    },
    // عناصر المدير فقط
    {
      to: "/admin",
      text: { ar: "لوحة المدير", en: "Admin Panel" },
      icon: "⚙️",
      roles: ["admin"],
    },
    {
      to: "/admin/users",
      text: { ar: "إدارة المستخدمين", en: "Users Management" },
      icon: "👥",
      roles: ["admin"],
    },
    {
      to: "/admin/cases",
      text: { ar: "إدارة القضايا", en: "Cases Management" },
      icon: "📋",
      roles: ["admin"],
    },
  ];

  // تصفية العناصر بناءً على دور المستخدم
  const filteredItems = mainItems.filter(
    (item) => !item.roles || (user && item.roles.includes(user.role))
  );

  return (
    <>
      {/* Overlay للجوال */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-40 md:hidden backdrop-blur-sm"
          onClick={onClose}
        ></div>
      )}

      <aside
        className={`
        fixed md:relative top-0 left-0 h-full bg-gradient-to-b from-gray-900 via-gray-800 to-gray-900 text-gray-200 shadow-xl p-6 z-50
        transform transition-all duration-300 ease-in-out
        ${
          isOpen
            ? "translate-x-0 shadow-2xl"
            : "-translate-x-full md:translate-x-0"
        }
        w-64 flex-shrink-0
        ${lang === "ar" ? "text-right" : "text-left"}
        overflow-y-auto
      `}
      >
        {/* زر الإغلاق للجوال */}
        <button
          className="md:hidden absolute top-4 left-4 text-xl text-gray-400 hover:text-blue-400 transition-colors duration-200"
          onClick={onClose}
          aria-label={lang === "ar" ? "إغلاق القائمة" : "Close menu"}
        >
          ✕
        </button>

        {/* رأس السايدبار */}
        <div className="mb-8 pt-2">
          <h2 className="text-xl font-bold mb-2 text-blue-400">
            {lang === "ar" ? "القائمة" : "Menu"}
          </h2>
          {user && (
            <p className="text-sm text-gray-400">
              {lang === "ar" ? "مرحباً" : "Welcome"}, {user.name}
            </p>
          )}
        </div>

        {/* قائمة العناصر */}
        <nav>
          <ul className="space-y-2">
            {filteredItems.map((item, index) => (
              <li key={index}>
                <Link
                  to={item.to}
                  className={`flex items-center gap-3 px-3 py-3 rounded-lg transition-all duration-200 group
                    ${
                      activeItem === item.to
                        ? "bg-blue-600 text-white shadow-md"
                        : "hover:bg-gray-700 hover:text-blue-400"
                    }
                    ${lang === "ar" ? "flex-row-reverse" : ""}
                  `}
                  onClick={() => window.innerWidth < 768 && onClose()}
                >
                  <span className="text-lg">{item.icon}</span>
                  <span className="font-medium">
                    {item.text[lang === "ar" ? "ar" : "en"]}
                  </span>
                  <span
                    className={`flex-1 ${
                      lang === "ar" ? "text-left" : "text-right"
                    }`}
                  >
                    {activeItem === item.to && (
                      <span className="text-xs bg-white bg-opacity-20 px-2 py-1 rounded-full">
                        {lang === "ar" ? "نشط" : "Active"}
                      </span>
                    )}
                  </span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>

        {/* معلومات إضافية في الأسفل */}
        <div className="mt-8 pt-6 border-t border-gray-700 text-xs text-gray-500">
          <p className="mb-2">
            {lang === "ar"
              ? "منصة عدالة AI للمساعدة القانونية"
              : "Adalah AI Legal Assistance Platform"}
          </p>
          <p>v1.0.0</p>
          <p className="mt-2">
            {lang === "ar" ? "مصمم بتقنية React" : "Built with React"}
          </p>
        </div>
      </aside>
    </>
  );
};

export default Sidebar;
